mapper.select:
    select m.member_id, m.member_identification, i.inqb_id, i.inqb_title , i.inqb_content, i.inqb_date, i.inqb_complete, a.ansb_id
	from tbl_member m join tbl_inquireboard i on m.member_id=i.member_id left join tbl_answerboard a on i.inqb_id=a.inqb_id
	where i.inqb_id = #{inqbId}

mapper.update:
update tbl_inquireboard
set inqb_complete=0
where inqb_id=#{inqbId}

update tbl_inquireboard
set inqb_complete=1
where ansb_id=#{ansbId}
