mapper.select:
    select m.member_id, m.member_identification, i.inqb_id, i.inqb_title , i.inqb_content, i.inqb_date, i.inqb_complete, a.ansb_id, a.ansb_Title, a.ansb_Content
	from tbl_member m join tbl_inquireboard i on m.member_id=i.member_id left join tbl_answerboard a on i.inqb_id=a.inqb_id
	where i.inqb_id = #{inqbId}

    select i.inqb_id, i.inqb_title , i.inqb_content, i.inqb_date, i.inqb_complete, a.ansb_id, a.ansb_title, a.ansb_content, a.ansb_date
	from tbl_inquireboard i join tbl_answerboard a on i.inqb_id=a.inqb_id
	where ansb_id= #{ansb_Id}

mapper.update:
update tbl_inquireboard
set inqb_complete=0
where inqb_id=#{inqbId}

update tbl_inquireboard
set inqb_complete=1
where inqb_id=#{inqbId}


update tbl_answerboard
set ansb_title = #{ansbTitle}, 
ansb_content = #{ansbContent}
where ansb_id = #{ansbId}
